import React, { FC, useContext, ChangeEvent } from 'react';
import { FormContext } from './Form';
/** @jsx jsx */
import { css, jsx } from '@emotion/core';
import { fontFamily, fontSize, gray5, gray2, gray6 } from './Styles';

interface Props {
  name: string;
  label?: string;
  type?: 'Text' | 'TextArea' | 'Password';
}

const baseCSS = css`
  box-sizing: border-box;
  font-family: ${fontFamily};
  font-size: ${fontSize};
  margin-bottom: 5px;
  padding: 8px 10px;
  border: 1px solid ${gray5};
  color: ${gray2};
  background-color: white;
  width: 100%;
  :focus {
    outline-color: ${gray5};
  }
  :disabled {
    background-color: ${gray6};
  }
`;

export const Field: FC<Props> = ({ name, label, type = 'Text' }) => {
  const { setValue, touched, validate } = useContext(FormContext);

  const handleChange = (
    e: ChangeEvent<HTMLInputElement> | ChangeEvent<HTMLTextAreaElement>,
  ) => {
    if (setValue) {
      setValue(name, e.currentTarget.value);
    }
    if (touched[name]) {
      if (validate) {
        validate(name);
      }
    }
  };
  return (
    <FormContext.Consumer>
      {(context) => (
        <div
          css={css`
            display: flex;
            flex-direction: column;
            margin-bottom: 15px;
          `}
        >
          {label && (
            <label
              css={css`
                font-weight: bold;
              `}
              htmlFor={name}
            >
              {label}
            </label>
          )}
          {(type === 'Text' || type === 'Password') && (
            <input
              type={type.toLowerCase()}
              id={name}
              value={
                context.values[name] === undefined ? '' : context.values[name]
              }
              onChange={handleChange}
              css={css`
                ${baseCSS};
              `}
            />
          )}
          {type === 'TextArea' && (
            <textarea
              id={name}
              value={
                context.values[name] === undefined ? '' : context.values[name]
              }
              onChange={handleChange}
              css={css`
                ${baseCSS};
                height: 100px;
              `}
            />
          )}
        </div>
      )}
    </FormContext.Consumer>
  );
};
